options.css:
.container {
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 20px;
  box-sizing: border-box;
}

.card {
  background: linear-gradient(135deg, #fa9200, #eddd53);
  border-radius: 15px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  width: 100%;
  max-width: 400px;
  height: 100%;
  padding: 20px 15px 15px;
  position: relative;
  box-shadow: 0 5px 15px rgba(0,0,0,0.1);
  display: flex;
  flex-direction: column;
  gap: 10px;
  transition: background 0.3s;
  box-sizing: border-box;
}

.card-icon-top {
  position: absolute;
  top: -30px;
  left: calc(50% - 30px);
  width: 60px;
  height: 60px;
}

.card-icon-top img {
  width: 100%;
  height: 100%;
  animation: pulse 2s ease-in-out infinite;
}

.card-title {
  margin-top: 40px;
  text-align: center;
  font-weight: bold;
  font-size: 16px;
  font-family: Inter, sans-serif;
  color: #000;
}

.card .info-line {
  display: flex;
  align-items: center;
  gap: 10px;
}

.card .info-line img {
  width: 24px;
  height: 24px;
}

.card .label {
  flex: 1;
  font-family: Inter, sans-serif;
  font-size: 14px;
  color: #000;
}

.card h3 {
  font-family: Inter, sans-serif;
  font-size: 16px;
  font-weight: bold;
  margin: 0;
  color: #000;
}

/* Cor para o texto do status online/offline */
.status-text {
  text-align: center;
  font-size: 20px;
  margin-top: auto;
  font-weight: bold;
}

.status-text.online {
  color: green;
}

.status-text.offline {
  color: red;
}

.card.trigger {
  background: linear-gradient(135deg, #ff4444, #ffd6d6);
}

@keyframes pulse {
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
}

