options.onRender:
// Função para converter bytes em unidades legíveis
function convertBytes(value) {
  const units = ["B", "KB", "MB", "GB", "TB", "PB"];
  let index = 0;
  while (value >= 1024 && index < units.length - 1) {
    value /= 1024;
    index++;
  }
  return `${formatValue(value)} ${units[index]}`;
}

// Formatação com 2 casas decimais
function formatValue(value) {
  return value.toFixed(2);
}

// Função para formatar uptime a partir de segundos
function formatUptime(seconds) {
  const days = Math.floor(seconds / (3600 * 24));
  const hours = Math.floor((seconds % (3600 * 24)) / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = Math.floor(seconds % 60);
  return `${days}D ${hours.toString().padStart(2, '0')}:${minutes
    .toString()
    .padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
}


if (htmlNode && data && data.series.length >= 7) {
  const card = htmlNode.getElementById('card_crmsyr');

  const temp = data.series[0]?.fields[1]?.values;
  const hd = data.series[1]?.fields[1]?.values;
  const up = data.series[2]?.fields[1]?.values;
  const down = data.series[3]?.fields[1]?.values;
  const uptimeHW = data.series[4]?.fields[1]?.values;
  const uptimeNet = data.series[5]?.fields[1]?.values;
  const online = data.series[6]?.fields[1]?.values;

  const lastTemp = temp?.get(temp.length - 1);
  const lastHD = hd?.get(hd.length - 1);
  const lastUp = up?.get(up.length - 1);
  const lastDown = down?.get(down.length - 1);
  const lastUptimeHW = uptimeHW?.get(uptimeHW.length - 1);
  const lastUptimeNet = uptimeNet?.get(uptimeNet.length - 1);
  const lastOnline = online?.get(online.length - 1);

  if (lastTemp != null) htmlNode.getElementById('value_temp').textContent = `${lastTemp.toFixed(0)}°`;
  if (lastHD != null) htmlNode.getElementById('value_hd').textContent = `${lastHD.toFixed(0)}%`;

  if (lastUp != null) htmlNode.getElementById('value_up').textContent = convertBytes(lastUp) + "/s";
  if (lastDown != null) htmlNode.getElementById('value_down').textContent = convertBytes(lastDown) + "/s";

  if (lastUptimeHW != null) htmlNode.getElementById('value_uptime_hw').textContent = formatUptime(lastUptimeHW);
  if (lastUptimeNet != null) htmlNode.getElementById('value_uptime_net').textContent = formatUptime(lastUptimeNet);

  if (lastOnline != null) {
    const onlineStatus = lastOnline === 1 ? 'Online' : 'Offline';
    const onlineEl = htmlNode.getElementById('value_online');
    onlineEl.textContent = onlineStatus;

    onlineEl.classList.remove('online', 'offline');
    if (lastOnline === 1) {
      onlineEl.classList.add('online');
    } else {
      onlineEl.classList.add('offline');
    }
  }

  // TRIGGER: alerta visual em caso de temperatura alta ou HD cheio
  card.classList.remove('trigger');
  if (lastTemp >= 60 || lastHD >= 90) {
    card.classList.add('trigger');
  }
}

