options.getOption:
let values = [];

// Coleta todos os valores numéricos de todas as séries
context.panel.data.series.forEach((s) => {
  const field = s.fields.find((f) => f.type === 'number');
  if (field && field.values) {
    values.push(...field.values);
  }
});

// Função para definir a cor com base no valor
function getColor(value) {
  if (value < 50) return '#4CAF50';    // Verde suave
  if (value < 80) return '#FFC107';    // Amarelo âmbar
  return '#F44336';                    // Vermelho elegante
}

// Pega os três primeiros valores normalizados
const data = values.slice(0, 3).map(v => v / 100);

// Define a cor de cada valor
const colors = values.slice(0, 3).map(v => getColor(v));

// Cor de fundo baseada no primeiro valor (ou um fallback)
const baseColor = getColor(values[0] || 0);

// Transforma cor hexadecimal em rgba com opacidade
function hexToRgba(hex, alpha) {
  const bigint = parseInt(hex.slice(1), 16);
  const r = (bigint >> 16) & 255;
  const g = (bigint >> 8) & 255;
  const b = bigint & 255;
  return `rgba(${r}, ${g}, ${b}, ${alpha})`;
}

return {
  series: [{
    type: 'liquidFill',
    radius: '80%',
    data: data,
    color: colors,
    outline: {
      show: false
    },
    backgroundStyle: {
      color: hexToRgba(baseColor, 0.2) // Cor dinâmica com transparência
    },
    label: {
      normal: {
        textStyle: {
          fontSize: 25,
          fontWeight: 'bold',
          color: '#FFFFFF' // Texto laranja
        }
      }
    }
  }]
};

